const axios = require('axios');

module.exports = {
  config: {
    name: "imgbb",
    version: "1.0",
    author: "Rakib Adil",
    countDown: 5,
    role: 0,
    shortDescription: {
      en: "Upload image to Imgbb"
    },
    longDescription: {
      en: "Upload image to Imgbb by replying to an image. This is a no prefix command made by Rakib Adil."
    },
    category: "tools",
    guide: {
      en: "Reply to an image to upload it."
    }
  },

  onStart: async function ({ api, event }) {
    console.log("imgbb command initialized.");
  },

  onChat: async function ({ api, event }) {
    const message = event.body.trim().toLowerCase();
    if (message !== "imgbb") return;

    const imageUrl = event.messageReply?.attachments[0]?.url;
    if (!imageUrl) {
      return api.sendMessage('Please reply to an image.', event.threadID, event.messageID);
    }

    try {
      const res = await axios.get(`https://rakib-imgbb-api.onrender.com/upload?url=${encodeURIComponent(imageUrl)}`);
      const { image } = res.data;

      return api.sendMessage(`âœ… **Image uploaded successfully!** ğŸ‰\n\nğŸ”— **Image URL**: ${image}\n\n- Powered by ğ—¥ğ—®ğ—¸ğ—¶ğ—¯ ğ—”ğ—±ğ—¶ğ—¹ğŸ‘‘`, event.threadID, event.messageID);
    } catch (error) {
      console.log(error);
      return api.sendMessage('Failed to upload image to Imgbb.', event.threadID, event.messageID);
    }
  }
};
